import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons"
import styled from "styled-components"

const Container = styled.div`  
    width: 100%;
    height: 100vh;
    display: flex;
    // background-color: coral;
    position: relative;
    overflow: hidden;
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=>props.direction === "left" && "10px"};
    right: ${props=>props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    // opacity: 0.5;
`;

const Wrapper = styled.div`
    height: 100%;
    // display: flex;
`;

const Slide = styled.div`
    width: 100vw;
    height:100vh;
    display: flex;
    align-items: center;
    background-color: #${props=>props.bg}
`;

const ImgContainer = styled.div`
    height:100%;
    flex:1;
`;

const Image = styled.img`
    height: 80%;
`;

const InfoContainer = styled.div`
    flex:1;
    padding:50px;
`;

const Title = styled.h1`
    font-size: 70px;
`;

const Description = styled.p`
    margin: 50px 0px;
    font-size:20px;
    font-weight: 500;
    letter-spacing: 3px;
`;

const Button = styled.button`
    padding: 10px;
    font-size: 20px;
    background-color: tranparant;
    cursor: pointer;
`;


const Slider = () => {
  return (
    <Container>
        <Arrow direction="left">
            <ArrowLeftOutlined/>
        </Arrow>

        <Wrapper>
            <Slide>

                <ImgContainer>
                    <Image src="https://cdn.pixabay.com/photo/2017/02/16/23/10/smile-2072907_960_720.jpg"/>
                </ImgContainer>

                <InfoContainer>
                    <Title>SUMMER SALE!</Title>
                    <Description>DON'T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.</Description>
                    <Button>SHOP NOW </Button>
                </InfoContainer>

            </Slide>

            <Slide>

                <ImgContainer>
                    <Image src="https://cdn.pixabay.com/photo/2017/02/16/23/10/smile-2072907_960_720.jpg"/>
                </ImgContainer>

                <InfoContainer>
                    <Title>SUMMER SALE!</Title>
                    <Description>DON'T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.</Description>
                    <Button>SHOP NOW </Button>
                </InfoContainer>

            </Slide>

            <Slide>

                <ImgContainer>
                    <Image src="https://media.istockphoto.com/photos/mother-and-little-daughter-at-home-picture-id1285783771?k=20&m=1285783771&s=612x612&w=0&h=WR2PzQaaHEkAgt0twtVDnjSCtyrpaOwv3mxdLOmP8-w="/>
                </ImgContainer>

                <InfoContainer>
                    <Title>SUMMER SALE!</Title>
                    <Description>DON'T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.</Description>
                    <Button>SHOP NOW </Button>
                </InfoContainer>

            </Slide>
        </Wrapper>


        <Arrow direction="right">
            <ArrowRightOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
