            <InfoContainer>
                <Title>Denim Jumpsuit</Title>
                <Description>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolore dolores aliquid, fugit, porro ipsum obcaecati minima voluptatum atque magnam, nostrum aperiam qui ea! Laborum.</Description>
                <Price>R249.99</Price>
            </InfoContainer>

        </Wrapper>

        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Product
